import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import PropTypes from 'prop-types';
import Helmet from 'react-helmet';
import { LOAD_POST_REQUEST } from '../reducers/post';

const Post = ({ id }) => {
    const { singlePost } = useSelector(state => state.post);
    const dispatch = useDispatch();

    // useEffect(() => {
    //   dispatch({
    //     type: LOAD_POST_REQUEST,
    //   });
    // }, []);

    return (
        <div>
          <Helmet
            title = {`${singlePost.User.nickname}님의 글`}
            description = {singlePost.content}
            meta = {[{
              name : 'description', content : singlePost.content,
            }, {
              property : 'og:title', content : `${singlePost.User.nickname}님의 게시글`,
            }, {
              property : 'og:description', content : singlePost.content,
            }, {
              property : 'og:image', content : singlePost.Images[0] && <img src={`http://localhost:7070/${post.Images[0].src}`}/>
            }, {
              property : 'og:url', content : `http://localhost:3060/post/${id}`,
            }]}
          />
          <div>{singlePost.content}</div>
          <div>{singlePost.User.nickname}</div>
          <div>
              {singlePost.Images[0] && <img src={`http://localhost:7070/${post.Images[0].src}`}/>}
          </div>
          {console.log(singlePost)};
        </div>
    );
};

Post.getInitialProps = async (context) => {
  const id = parseInt(context.query.id , 10)
  context.store.dispatch({
    type: LOAD_POST_REQUEST,
    data: id,
  });
  console.log('POST getInitialProps', id);
  return { id }
};

Post.propTypes = {
  id: PropTypes.number.isRequired,
};

export default Post;
